import React from "react";
import "./App.css";

function AddBook(props) {
  return (
    <div className="modal-main">
      <div className="modal-header">
        <button>X</button>
      </div>
      <div className="modal-form">
        <form>
          <label>Title</label>
          <input type="text" id="title" required></input>
          <label>Author</label>
          <input type="text" id="author" required></input>
          <label>ISBN</label>
          <input type="text" id="isbn" required></input>
          <label>Have you read the book?</label>
          <input type="checkbox" id="checkbox"></input>
          <button>+</button>
        </form>
      </div>
    </div>
  );
}

class Book extends React.Component {
  constructor(title, author, isbn, status) {
    super();
    this.title = title;
    this.author = author;
    this.isbn = isbn;
    this.status = status;
  }
}

const Header = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontSize: "30px",
        backgroundColor: "green"
      }}
    >
      <h3>Book List App</h3>
    </div>
  );
};

class BookList extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="main">
        <div className="main-headers">
          <p>Title</p>
          <p>Author</p>
          <p>ISBN</p>
          <p>READ?</p>
        </div>

        <div className="main-books"></div>

        <div className="main-inputs">
          <input type="text" placeholder="title" id="title"></input>
          <input type="text" placeholder="author" id="author"></input>
          <input type="text" placeholder="isbn" id="isbn"></input>
          <input type="checkbox" id="status"></input>
          <button id="submit-button">ADD</button>
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      visible: true
    };
  }

  changeState = () => {
    this.state.visible = false;
  };

  render() {
    return (
      <div className="MAINAPP">
        <div>{this.state.visible ? <AddBook /> : null}</div>
        <button
          onClick={() => {
            this.setState({ visible: !this.state.visible });
          }}
        >
          XXXX
        </button>
      </div>
    );
  }
}

export default App;
