import React from "react";
import "./App.css";

class AddBook extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      visible: true
    };
  }

  render() {
    const modal = (
      <div className="modal-main">
        <div className="modal-header">
          <button
            onClick={() => {
              this.setState({ visible: !this.state.visible });
            }}
          >
            X
          </button>
        </div>
        <div className="modal-form">
          <form>
            <label>Title</label>
            <input type="text" id="title" required></input>
            <label>Author</label>
            <input type="text" id="author" required></input>
            <label>ISBN</label>
            <input type="text" id="isbn" required></input>
            <label>Have you read the book?</label>
            <input type="checkbox" id="checkbox"></input>
            <button>+</button>
          </form>
        </div>
      </div>
    );

    return <div>{this.state.visible ? modal : null}</div>;
  }
}

class Book extends React.Component {
  constructor(title, author, isbn, status) {
    super();
    this.title = title;
    this.author = author;
    this.isbn = isbn;
    this.status = status;
  }
}

const Header = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontSize: "30px",
        backgroundColor: "green"
      }}
    >
      <h3>Book List App</h3>
    </div>
  );
};

class BookList extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <table>
          <thead>Title</thead>
          <thead>Author</thead>
          <thead>ISBN</thead>
          <thead>Read</thead>
        </table>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      visible: false
    };
  }

  changeState = () => {
    this.state.visible = false;
  };

  render() {
    return (
      <div className="MAINAPP">
        <Header />
        <BookList />
        {this.state.visible ? <AddBook /> : null}
        <button
          onClick={() => {
            this.setState({ visible: !this.state.visible });
          }}
        >
          XXXXXXXX
        </button>
      </div>
    );
  }
}

export default App;
